mapper.select:
		SELECT n_idx,n_writer , writer_idx, n_receiver, receiver_idx, n_title, n_content, n_date, n_confirm, n_new FROM (
			SELECT n_idx,n_writer , writer_idx, n_receiver, receiver_idx, n_title, n_content, n_date, n_confirm, n_new, ROW_NUMBER() OVER(ORDER BY n_idx DESC) AS rnum FROM note
  		WHERE receiver_idx=#{param3})WHERE rnum BETWEEN #{param1} AND #{param2}

		SELECT n_idx,n_writer , writer_idx, n_receiver, receiver_idx, n_title, n_content, n_date, n_confirm, n_new FROM (
			SELECT n_idx,n_writer , writer_idx, n_receiver, receiver_idx, n_title, n_content, n_date, n_confirm, n_new, ROW_NUMBER() OVER(ORDER BY n_idx DESC) AS rnum FROM note
  		WHERE ${param4} like '%'||#{param3}||'%' AND receiver_idx=#{param5})WHERE rnum BETWEEN #{param1} AND #{param2}
